import React from "react";

export default function ProgressBar({ percent, session }) {
  return (
    <div className="row mb-2">
      <div className="col">
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={session.percent} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: `${session.percent}%` }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  );
}
