import React from "react";
import { secondsToDuration } from "../../utils/duration";

export default function SessionTitle({ session, percentage, sessionInfo, nextSession }) {
  /* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */

  const style = !session ? {display: 'none'} : {display: null};


  return (
    <div style={style}>
      {/* TODO: This area should show only when there is an active focus or break - i.e. the session is running or is paused */}
      <div className="row mb-2">
        <div className="col">
          {/* TODO: Update message below to include current session (Focusing or On Break) total duration */}
          <h2 data-testid="session-title">
            {session?.label} for 25:00 minutes
          </h2>
          {/* âœ” TODO: Update message below correctly format the time remaining in the current session */}
          <p className="lead" data-testid="session-sub-title">
            {secondsToDuration(session?.timeRemaining)} remaining
          </p>
        </div>
      </div>
      <div className="row mb-2">
        <div className="col">
          <div className="progress" style={{ height: "20px" }}>
            <div
              className="progress-bar"
              role="progressbar"
              aria-valuemin="0"
              aria-valuemax="100"
              aria-valuenow={percentage} // TODO: Increase aria-valuenow as elapsed time increases
              style={{ width: `${percentage}%` }} // TODO: Increase width % as elapsed time increases
            />
          </div>
        </div>
      </div>
    </div>
  );
}
